export class WaterFall{ //类
    constructor(options){  //构造函数
        this.$options = options  
        this.miniHeight = []    //最小高度数组
        this.__init()
    }


    __init(){   //初始化
        this.items = document.querySelector(this.$options.el).children  //获取wf-item数组（每张图外面的div）
        this.column = this.$options.column  //获取在LocalImg.vue构造的列数column
        this.gap = this.$options.gap    //获取在LocalImg.vue构造的间隔 gap
        //获取单张图片的宽度（wf-content宽度减去列数倍间隔 除以总共的列数，得到1列的宽度）
        this.itemWidth = (document.querySelector(this.$options.el).offsetWidth - this.gap*5)/this.column
        this.__render()
    }

    __render(){ //渲染
        [...this.items].forEach((value,index)=>{    //遍历每张图外面的div
            value.style.width = this.itemWidth +"px"
            if(index < this.column){    //第一行 0-4
                value.style.top = "0px"
                value.style.left = (this.itemWidth + this.gap)*index +"px"
                this.miniHeight.push(value.offsetHeight)
            }else{  //第二行往后
                this.minIndex = this.__getMiniIndex()
                value.style.top = this.miniHeight[this.minIndex]
                value.style.left = (this.itemWidth + this.gap)*this.minIndex
                this.miniHeight[this.minIndex] += value.style.offsetHeight
            }
        })
    }

    __getMiniIndex(){
        return this.miniHeight.indexOf(Math.min(...this.miniHeight))
    }
}
