import Vue from 'vue'
import Vuex from 'vuex'
Vue.use(Vuex)
//用于响应组件中的动作
const actions={}
//用于操作数据
const mutations={}  // 在mutations里面不要写业务逻辑 Ajax请求
//用于存储数据
const state={}
// 创建store
export default new Vuex.Store({
    actions,
    mutations,
    state
})
